    void filterSearch(String text){
        foodList.clear();
        for(Food food : allFoodList){
            if(food.getFoodName().toLowerCase().contains(text.toLowerCase().trim())){
                foodList.add(food);
            }
        }
        searchAdapter = new SearchAdapter(foodList, this);
        foodListRecycler.setLayoutManager(new LinearLayoutManager(this));
        foodListRecycler.setAdapter(searchAdapter);
    }

